import React, { useState } from "react";

export default function TextForm(props) {
    const [text, setText] = useState("Enter Text Here !.");

    const onbtnClick = () => {
        console.log("Click event fired !");
        setText(text.toUpperCase());
    };

    const txtBoxChange = (event) => {
        console.log("Onchange Event fired");
        setText(event.target.value);
    };

    return (
        <>
            <div className="container">
                <div className="mb-3">
                    <label htmlFor="myBox" className="form-label">
                        {props.heading}
                    </label>
                    <textarea
                        onChange={txtBoxChange}
                        className="form-control"
                        style={{ backgroundColor: props.mode === 'light' ? 'white' : 'gray', color: props.mode === 'light' ? 'black' : 'white' }}
                        value={text}
                        id="myBox"
                        rows="10"
                    ></textarea>
                </div>
                <button className="btn btn-primary" onClick={onbtnClick}>
                    Convert to Upper Case
                </button>
            </div>
            <div className="container" id="myDiv">
                <h2>Text Summary</h2>
                <p>Total Words {text.split(" ").length} and {text.length} Characters</p>
            </div>
        </>
    );
}
