import React from "react";

export default function TextForm(props) {
  return (
    <div className="mb-3">
      <label htmlFor="myBox" className="form-label">
        {props.heading}
      </label>
      <textarea
        className="form-control"
        id="myBox"
        rows="3"
      ></textarea>
      <button className="btn btn-primary">Convert to Upper Case</button>
    </div>
  );
}
